import { Component, OnInit, ViewChild } from '@angular/core';
import { BookingRoomsService } from 'src/app/services/booking-rooms/booking-rooms.service';
import { AddBookingComponent } from './add-booking/add-booking.component';

declare var bootstrap: any;

@Component({
  selector: 'app-booking-rooms',
  templateUrl: './booking-rooms.component.html',
  styleUrls: ['./booking-rooms.component.css']
})
export class BookingRoomsComponent {

  bookings: any[] = [];
  selectedBooking: any;
  isEditMode: boolean = false;

  @ViewChild(AddBookingComponent) addEditBookingModal!: AddBookingComponent;

  constructor(private bookingRoomService: BookingRoomsService) {}

  ngOnInit(): void {
    this.loadBookings();
  }

  loadBookings() {
    this.bookingRoomService.getBookings().subscribe((bookings) => {
      this.bookings = bookings;
    });
  }

  openAddBookingModal() {
    this.isEditMode = false;
    this.selectedBooking = null;
    const modalElement = document.getElementById('addEditBookingModal');
    const modal = new bootstrap.Modal(modalElement);
    modal.show();
  }

  openEditBookingModal(booking: any) {
    this.isEditMode = true;
    this.selectedBooking = booking;
    const modalElement = document.getElementById('addEditBookingModal');
    const modal = new bootstrap.Modal(modalElement);
    modal.show();
  }

  deleteBooking(id: number) {
    this.bookingRoomService.deleteBooking(id).subscribe(() => {
      this.loadBookings();
    });
  }

  onBookingUpdated() {
    this.loadBookings();
  }
}
<div class="pagetitle">
  <h1>Bookings</h1>
  <nav>
    <ol class="breadcrumb">
      <li class="breadcrumb-item"><a href="index.html">Home</a></li>
      <li class="breadcrumb-item">Bookings</li>
      <li class="breadcrumb-item active">Bookings Tables</li>
    </ol>
  </nav>
</div>

<section class="section">
  <div class="row">
    <div class="col-lg-12">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">
            Datatables
            <button type="button" class="btn btn-primary" (click)="openAddBookingModal()">Add Booking</button>
          </h5>

          <!-- Table displaying Bookings -->
          <table class="table datatable">
            <thead>
              <tr>
                <th>User ID</th>
                <th>Room ID</th>
                <th>Start Date</th>
                <th>End Date</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let booking of bookings">
                <td>{{ booking.user_id }}</td>
                <td>{{ booking.room_id }}</td>
                <td>{{ booking.start_date }}</td>
                <td>{{ booking.end_date }}</td>
                <td>{{ booking.status }}</td>
                <td>
                  <button type="button" class="btn btn-primary" (click)="openEditBookingModal(booking)">Edit</button>
                  <button type="button" class="btn btn-danger" (click)="deleteBooking(booking.booking_id)">Delete</button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</section>

<!-- Modal for Adding/Editing Booking -->
<app-add-booking
  [booking]="selectedBooking"
  [isEditMode]="isEditMode"
  (bookingUpdated)="onBookingUpdated()">
</app-add-booking>
