import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PgUserService } from '../pg-user.service';
@Component({
  selector: 'app-add-user',
  templateUrl: './add-user.component.html',
  styleUrls: ['./add-user.component.css']
})
export class AddUserComponent implements OnInit {

  @Input() user: any;
  pgUserForm!: FormGroup;
  isEditMode: boolean = false;

  @Input() isEditMode: boolean = false;
  @Input() pgUser: any;
  pgUserForm: FormGroup;

  constructor(public activeModal: NgbActiveModal, private fb: FormBuilder) {}

  ngOnInit(): void {
    this.pgUserForm = this.fb.group({
      name: ['', Validators.required],
      ext: ['', Validators.required],
      city: ['', Validators.required],
      startDate: ['', Validators.required],
      completion: ['', Validators.required]
    });

    if (this.isEditMode && this.pgUser) {
      this.pgUserForm.patchValue(this.pgUser);
    }
  }

  onSubmit(): void {
    if (this.pgUserForm.valid) {
      const userData = this.pgUserForm.value;
      this.activeModal.close(userData);
    }
  }
}
