import { Component, Input, Output, EventEmitter, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PaymentService } from 'src/app/services/payment/payment.service';


@Component({
  selector: 'app-add-payment',
  templateUrl: './add-payment.component.html',
  styleUrls: ['./add-payment.component.css']
})
export class AddPaymentComponent implements OnInit {
  @Input() payment: any = {};
  @Input() isEditMode: boolean = false;
  @Output() paymentUpdated = new EventEmitter<void>();
  paymentForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private paymentService: PaymentService
  ) {
    this.paymentForm = this.fb.group({
      booking_id: ['', Validators.required],
      amount: ['', [Validators.required, Validators.min(0)]],
      payment_date: ['', Validators.required],
      payment_method: ['', Validators.required],
      status: ['', Validators.required],
    });
  }

  ngOnInit(): void {
    if (this.isEditMode && this.payment) {
      this.paymentForm.patchValue(this.payment);
    }
  }

  submitForm() {
    if (this.paymentForm.invalid) {
      return;
    }

    if (this.isEditMode) {
      const updatedPayment = { ...this.payment, ...this.paymentForm.value };
      this.paymentService.updatePayment(updatedPayment).subscribe(() => {
        this.paymentUpdated.emit();
        this.closeModal();
      });
    } else {
      this.paymentService.createPayment(this.paymentForm.value).subscribe(() => {
        this.paymentUpdated.emit();
        this.closeModal();
      });
    }
  }

  closeModal() {
    const modalElement = document.getElementById('addEditPaymentModal');
    const modal = bootstrap.Modal.getInstance(modalElement);
    modal.hide();
  }
}
<div class="modal fade" id="addEditPaymentModal" tabindex="-1" aria-labelledby="addEditPaymentModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="addEditPaymentModalLabel">{{ isEditMode ? 'Edit Payment' : 'Add Payment' }}</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form (ngSubmit)="submitForm()">
          <!-- Add your form fields here -->
          <div class="mb-3">
            <label for="booking_id" class="form-label">Booking ID</label>
            <input type="number" class="form-control" id="booking_id" [(ngModel)]="payment.booking_id" name="booking_id" required>
          </div>
          <div class="mb-3">
            <label for="amount" class="form-label">Amount</label>
            <input type="number" class="form-control" id="amount" [(ngModel)]="payment.amount" name="amount" required>
          </div>
          <div class="mb-3">
            <label for="payment_date" class="form-label">Payment Date</label>
            <input type="date" class="form-control" id="payment_date" [(ngModel)]="payment.payment_date" name="payment_date" required>
          </div>
          <div class="mb-3">
            <label for="payment_method" class="form-label">Payment Method</label>
            <select class="form-control" id="payment_method" [(ngModel)]="payment.payment_method" name="payment_method" required>
              <option value="cash">Cash</option>
              <option value="credit_card">Credit Card</option>
              <option value="debit_card">Debit Card</option>
              <option value="net_banking">Net Banking</option>
            </select>
          </div>
          <div class="mb-3">
            <label for="status" class="form-label">Status</label>
            <select class="form-control" id="status" [(ngModel)]="payment.status" name="status" required>
              <option value="paid">Paid</option>
              <option value="pending">Pending</option>
              <option value="failed">Failed</option>
            </select>
          </div>
          <button type="submit" class="btn btn-primary">{{ isEditMode ? 'Update' : 'Add' }}</button>
        </form>
      </div>
    </div>
  </div>
</div>
