import { Component, OnInit } from '@angular/core';
import { PgUserService } from 'src/app/services/pg-user/pg-user.service';
import { AddUserComponent } from './add-user/add-user.component';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
@Component({
  selector: 'app-pg-user',
  templateUrl: './pg-user.component.html',
  styleUrls: ['./pg-user.component.css']
})
export class PgUserComponent implements OnInit  {
  pgUsers: any[] = [];
  newUser: any = {}; // Initialize with appropriate structure
  editUser: any = null; // Store the user being edited
  constructor(private modalService: NgbModal) {}

  ngOnInit(): void {
    // Initialize the pgUsers list
  }

  openAddPGUserModal(): void {
    const modalRef = this.modalService.open(AddUserComponent);
    modalRef.componentInstance.isEditMode = false;
  }

  openEditPGUserModal(user: any): void {
    const modalRef = this.modalService.open(AddUserComponent);
    modalRef.componentInstance.isEditMode = true;
    modalRef.componentInstance.pgUser = user;
  }

  deletePGUser(userId: number): void {
    this.pgUsers = this.pgUsers.filter(user => user.id !== userId);
  }
}
