import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm: FormGroup;
  loginError = false;

  constructor(private fb: FormBuilder, private authService: AuthService, private toastr: ToastrService) {
    this.loginForm = this.fb.group({
      username: ['', [Validators.required]],
      password: ['', [Validators.required]],
      remember: [false]
    });
  }

  ngOnInit(): void {}

  onSubmit(): void {
    if (this.loginForm.valid) {
      const { username, password } = this.loginForm.value;
      if (!this.authService.login(username, password)) {
        this.loginError = true;
        this.toastr.error('Invalid username or password', 'Login Failed', { timeOut: 3000, positionClass: 'toast-top-right', closeButton: true, progressBar: true });
      }
    } else {
      this.showFieldErrors();
    }
  }

  showFieldErrors(): void {
    Object.keys(this.loginForm.controls).forEach(field => {
      const control = this.loginForm.get(field);
      if (control && control.invalid && (control.dirty || control.touched)) {
        this.toastr.error(`Invalid ${field}`, 'Validation Error', { timeOut: 3000, positionClass: 'toast-top-right', closeButton: true, progressBar: true });
      }
    });
  }
}
