import { Component, OnInit, ViewChild } from '@angular/core';
import { PaymentService } from 'src/app/services/payment/payment.service';
import { AddPaymentComponent } from './add-payment/add-payment.component';

declare var bootstrap: any;

@Component({
  selector: 'app-payment',
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.css']
})
export class PaymentComponent implements OnInit {
  payments: any[] = [];
  selectedPayment: any;
  isEditMode: boolean = false;

  @ViewChild(AddPaymentComponent) addEditPaymentModal!: AddPaymentComponent;

  constructor(private paymentService: PaymentService) {}

  ngOnInit(): void {
    this.loadPayments();
  }

  loadPayments() {
    this.paymentService.getPayments().subscribe((payments) => {
      this.payments = payments;
    });
  }

  openAddPaymentModal() {
    this.isEditMode = false;
    this.selectedPayment = null;
    const modalElement = document.getElementById('addEditPaymentModal');
    const modal = new bootstrap.Modal(modalElement);
    modal.show();
  }

  openEditPaymentModal(payment: any) {
    this.isEditMode = true;
    this.selectedPayment = payment;
    const modalElement = document.getElementById('addEditPaymentModal');
    const modal = new bootstrap.Modal(modalElement);
    modal.show();
  }

  deletePayment(payment_id: number) {
    this.paymentService.deletePayment(payment_id).subscribe(() => {
      this.loadPayments();
    });
  }

  onPaymentUpdated() {
    this.loadPayments();
  }
}
<div class="pagetitle">
  <h1>Payment Tables</h1>
  <nav>
    <ol class="breadcrumb">
      <li class="breadcrumb-item"><a routerLink="/dashboard">Home</a></li>
      <li class="breadcrumb-item">Payments</li>
      <li class="breadcrumb-item active">Payment Tables</li>
    </ol>
  </nav>
</div>

<section class="section">
  <div class="row">
    <div class="col-lg-12">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title d-flex justify-content-between align-items-center">
            <span>Payment's List</span>
            <button type="button" class="btn btn-primary" (click)="openAddPaymentModal()">Add Payment</button>
          </h5>

          <!-- Table displaying payments -->
          <table class="table datatable">
            <thead>
              <tr>
                <th>Booking ID</th>
                <th>Amount</th>
                <th>Payment Date</th>
                <th>Payment Method</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let payment of payments">
                <td>{{ payment.booking_id }}</td>
                <td>{{ payment.amount }}</td>
                <td>{{ payment.payment_date }}</td>
                <td>{{ payment.payment_method }}</td>
                <td>{{ payment.status }}</td>
                <td>
                  <button type="button" class="btn btn-primary" (click)="openEditPaymentModal(payment)">Edit</button>
                  <button type="button" class="btn btn-danger" (click)="deletePayment(payment.payment_id)">Delete</button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</section>

<!-- Modal for Adding/Editing Payment -->
<app-add-payment
  [payment]="selectedPayment"
  [isEditMode]="isEditMode"
  (paymentUpdated)="onPaymentUpdated()">
</app-add-payment>
