import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../auth.service';
import { NotificationService } from '../../services/notification.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit {
  signupForm!: FormGroup;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private notificationService: NotificationService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.signupForm = this.fb.group({
      name: ['', [Validators.required]],
      email: ['', [Validators.required, Validators.email]],
      username: ['', [Validators.required]],
      password: ['', [Validators.required]],
      terms: [false, [Validators.requiredTrue]]
    });
  }

  onSubmit(): void {
    if (this.signupForm.valid) {
      const user = this.signupForm.value;
      this.authService.register(user).subscribe(
        response => {
          this.notificationService.success('Registration successful');
          this.router.navigate(['/login']);
        },
        error => {
          this.notificationService.error('Registration failed');
        }
      );
    } else {
      this.showFieldErrors();
    }
  }

  showFieldErrors(): void {
    Object.keys(this.signupForm.controls).forEach(field => {
      const control = this.signupForm.get(field);
      if (control && control.invalid && (control.dirty || control.touched)) {
        this.notificationService.error(`Invalid ${field}`);
      }
    });
  }
}
<div class="container">
  <section class="section register min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
    <div class="container">
      <div class="row justify-content-center">
        <div class="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">
          <div class="d-flex justify-content-center py-4">
            <a routerLink="/" class="logo d-flex align-items-center w-auto">
              <img src="assets/img/logo.png" alt="">
              <span class="d-none d-lg-block">PG Admin</span>
            </a>
          </div>
          <div class="card mb-3">
            <div class="card-body">
              <div class="pt-4 pb-2">
                <h5 class="card-title text-center pb-0 fs-4">Create an Account</h5>
                <p class="text-center small">Enter your personal details to create account</p>
              </div>
              <form [formGroup]="signupForm" (ngSubmit)="onSubmit()" class="row g-3 needs-validation" novalidate>
                <div class="col-12">
                  <label for="yourName" class="form-label">Your Name</label>
                  <input type="text" formControlName="name" class="form-control" id="yourName" required>
                  <div *ngIf="signupForm.get('name')?.invalid && signupForm.get('name')?.touched" class="invalid-feedback">
                    Please, enter your name!
                  </div>
                </div>
                <div class="col-12">
                  <label for="yourEmail" class="form-label">Your Email</label>
                  <input type="email" formControlName="email" class="form-control" id="yourEmail" required>
                  <div *ngIf="signupForm.get('email')?.invalid && signupForm.get('email')?.touched" class="invalid-feedback">
                    Please enter a valid Email address!
                  </div>
                </div>
                <div class="col-12">
                  <label for="yourUsername" class="form-label">Username</label>
                  <div class="input-group has-validation">
                    <span class="input-group-text" id="inputGroupPrepend">@</span>
                    <input type="text" formControlName="username" class="form-control" id="yourUsername" required>
                    <div *ngIf="signupForm.get('username')?.invalid && signupForm.get('username')?.touched" class="invalid-feedback">
                      Please choose a username.
                    </div>
                  </div>
                </div>
                <div class="col-12">
                  <label for="yourPassword" class="form-label">Password</label>
                  <input type="password" formControlName="password" class="form-control" id="yourPassword" required>
                  <div *ngIf="signupForm.get('password')?.invalid && signupForm.get('password')?.touched" class="invalid-feedback">
                    Please enter your password!
                  </div>
                </div>
                <div class="col-12">
                  <div class="form-check">
                    <input class="form-check-input" type="checkbox" formControlName="terms" id="acceptTerms" required>
                    <label class="form-check-label" for="acceptTerms">I agree and accept the <a href="#">terms and conditions</a></label>
                    <div *ngIf="signupForm.get('terms')?.invalid && signupForm.get('terms')?.touched" class="invalid-feedback">
                      You must agree before submitting.
                    </div>
                  </div>
                </div>
                <div class="col-12">
                  <button class="btn btn-primary w-100" type="submit">Create Account</button>
                </div>
                <div class="col-12">
                  <p class="small mb-0">Already have an account? <a routerLink="/login">Log in</a></p>
                </div>
              </form>
            </div>
          </div>
          <div class="credits">
            Designed by <a href="https://bootstrapmade.com/">BootstrapMade</a>
          </div>
        </div>
      </div>
    </div>
  </section>
</div>
