import { Component, OnInit } from '@angular/core';
import * as echarts from 'echarts';
@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  totalRooms = 50;
  bookedRooms = 30;
  availableRooms = 20;
  totalGuests = 45;
  newBookings = 5;
  pendingInquiries = 2;
  revenue = [2000,7000,8000,10000];
  recentBookings = [
    { details: 'Booking 1 details' },
    { details: 'Booking 2 details' }
  ];
  upcomingActivities = [
    { details: 'Activity 1 details' },
    { details: 'Activity 2 details' }
  ];

  isDropdownOpen = false;

  cards = [
    { title: 'Total Rooms', value: this.totalRooms },
    { title: 'Booked Rooms', value: this.bookedRooms },
    { title: 'Available Rooms', value: this.availableRooms },
    { title: 'Total Guests', value: this.totalGuests },
    { title: 'New Bookings', value: this.newBookings },
    { title: 'Pending Inquiries', value: this.pendingInquiries },
    { title: 'Revenue', value: this.revenue }
  ];

  quickActions = [
    { label: 'Book a Room', link: '/book' },
    { label: 'View Rooms', link: '/rooms' },
    { label: 'Add New Room', link: '/add-room' },
    { label: 'Manage Guests', link: '/manage-guests' },
    { label: 'Send Notifications', link: '/send-notifications' }
  ];

  constructor() { }

  ngOnInit(): void {
    this.initRoomsChart();
    this.initRevenueChart();
  }

  toggleDropdown() {
    this.isDropdownOpen = !this.isDropdownOpen;
  }

  initRoomsChart() {
    const roomsChart = echarts.init(document.getElementById('roomsChart') as HTMLDivElement);

    const roomsOption = {
      title: {
        text: 'Rooms Overview'
      },
      tooltip: {},
      legend: {
        data: ['Rooms']
      },
      xAxis: {
        data: ['Total Rooms', 'Booked Rooms', 'Available Rooms']
      },
      yAxis: {},
      series: [{
        name: 'Rooms',
        type: 'bar',
        data: [this.totalRooms, this.bookedRooms, this.availableRooms]
      }]
    };

    roomsChart.setOption(roomsOption);
  }

  initRevenueChart() {
    const revenueChart = echarts.init(document.getElementById('revenueChart') as HTMLDivElement);

    const revenueOption = {
      title: {
        text: 'Revenue Overview'
      },
      tooltip: {},
      legend: {
        data: ['Revenue']
      },
      xAxis: {
        type: 'category',
        data: ['Revenue']
      },
      yAxis: {
        type: 'value'
      },
      series: [{
        name: 'Revenue',
        type: 'scatter',
        smooth: true,
        data: [this.revenue]
      }]
    };

    revenueChart.setOption(revenueOption);
  }
}

<div class="dashboard">
  <h2>Dashboard</h2>


  <!-- Existing cards -->
  <div class="row">
    <div class="col-md-8">
      <div class="row">
        <div class="col-md-4 mb-4" *ngFor="let card of cards">
          <div class="card">
            <div class="card-body">
              <h3 class="card-title">{{ card.title }}</h3>
              <p class="card-text">{{ card.value }}</p>
            </div>
          </div>
        </div>
      </div>
    </div>

    <!-- Quick Actions -->
    <div class="col-md-4">
      <div class="dropdown">
        <button (click)="toggleDropdown()" class="dropdown-button">
          <i class="bi bi-plus-circle"></i> Quick Actions
        </button>
        <ul [ngClass]="{ 'show': isDropdownOpen, 'hide': !isDropdownOpen }" class="card" style="width: 208px;">
          <li class="card-body" *ngFor="let action of quickActions">
            <a [routerLink]="action.link">
              <i class="bi bi-arrow-right"></i> {{ action.label }}
            </a>
          </li>
        </ul>
      </div>
    </div>
  </div>


  <!-- ECharts Container -->
  <div class="row">
    <div class="col-lg-6">
      <div class="card mb-4">
        <div class="card-body">
          <h3 class="card-title">Rooms Overview</h3>
          <div id="roomsChart" style="width: 100%; height: 400px;"></div>
        </div>
      </div>
    </div>

    <div class="col-lg-6">
      <div class="card mb-4">
        <div class="card-body">
          <h3 class="card-title">Revenue Overview</h3>
          <div id="revenueChart" style="width: 100%; height: 400px;"></div>
        </div>
      </div>
    </div>
  </div>

  <!-- Recent Bookings Card -->
  <!-- Upcoming Check-ins/Check-outs Card -->
  <div class="row">
    <div class="col-lg-6">
      <div class="card mb-4">
        <div class="card-body">
          <h3 class="card-title">Recent Bookings</h3>
          <ul class="list-unstyled">
            <li *ngFor="let booking of recentBookings">{{ booking.details }}</li>
          </ul>
        </div>
      </div>
    </div>

    <div class="col-lg-6">
      <div class="card">
        <div class="card-body">
          <h3 class="card-title">Upcoming Check-ins/Check-outs</h3>
          <ul class="list-unstyled">
            <li *ngFor="let activity of upcomingActivities">{{ activity.details }}</li>
          </ul>
        </div>
      </div>
    </div>
  </div>
</div>
